mapper.select:


SELECT commentId
FROM comments 
WHERE 1=1
and commentId = #{commendId}
and EXISTS (
    SELECT 1 
    FROM comments c2 
    WHERE c2.parentId = comments.commentId
);

mapper.update:
		UPDATE Comments 
 	SET content = #{content}, updatedAt = GETDATE()
 	WHERE commentId = #{commentId}

		UPDATE Comments 
 	SET deleteYn = 'Y'
 	WHERE commentId = #{commentId} 
    		OR parentId IN (
        		SELECT commentId FROM Comments WHERE parentId = #{commentId}
    			)
