mapper.select:
SELECT b.seq, b.sub, b.division, b.context, b.uId, b.usem, b.processState, c.codeName AS processState, b.manager, b.requestDate, b.completeDate FROM board b inner join code_master c on b.processState = c.codeId
b.manager LIKE '%' + #{manager} + '%'
AND b.requestDate >= #{startDate}
AND b.requestDate <= #{endDate}
AND b.sub LIKE '%' + #{sub} + '%'
ORDER BY b.seq DESC
