mapper.select:
SELECT b.seq, b.sub, d.CORP_NM as division, d.DEPT_NM as dpNm, b.context, b.uId, b.usem, b.processState, c.codeName AS statusNm, b.manager, b.managerId, b.managerTel, b.managerEmail, b.requestDate, b.requestDate AS requestDateTime, b.completeDate, b.completeDt, b.srFlag, b.saveFlag, (SELECT codeName FROM CODE_MASTER WHERE codeId = b.inquiryType) as inquiryType, b.inquiryPart, b.priority, (SELECT COUNT(*) FROM COMMENTS WHERE postid = b.seq) as countComment FROM board b left join code_master c on b.processState = c.codeId left outer join SPG.DBO.CRM_USER_MASTER d on b.writerId = d.USR_ID
b.deleteYn = 'N'
AND b.requestDate >= #{startDate}
AND b.requestDate <= #{endDate}
AND b.writerId = #{writerId}
AND b.managerId = #{managerId}
ORDER BY b.seq DESC

SELECT b.seq, b.sub, d.CORP_NM as division, d.DEPT_NM as dpNm, b.context, b.uId, b.usem, b.processState, c.codeName AS statusNm, b.manager, b.managerId, b.managerTel, b.managerEmail, b.requestDate, b.requestDate AS requestDateTime, b.completeDate, b.completeDt, b.srFlag, b.saveFlag, (SELECT codeName FROM CODE_MASTER WHERE codeId = b.inquiryType) as inquiryType, b.inquiryPart, b.priority, (SELECT COUNT(*) FROM COMMENTS WHERE postid = b.seq) as countComment FROM board b INNER JOIN sys_empref e ON b.writerId = e.id left join code_master c on b.processState = c.codeId left outer join SPG.DBO.CRM_USER_MASTER d on b.writerId = d.USR_ID
b.deleteYn = 'N'
AND b.requestDate >= #{startDate}
AND b.requestDate <= #{endDate}
and e.depart1 = ( SELECT depart1 FROM sys_empref WHERE id = #{writerId} )
ORDER BY b.seq DESC

SELECT b.seq, b.sub, d.CORP_NM as division, d.DEPT_NM as dpNm, b.context, b.uId, b.usem, b.processState, c.codeName AS statusNm, b.manager, b.managerId, b.managerTel, b.managerEmail, b.requestDate, b.requestDate AS requestDateTime, b.completeDate, b.completeDt, b.srFlag, b.saveFlag, (SELECT codeName FROM CODE_MASTER WHERE codeId = b.inquiryType) as inquiryType, b.inquiryPart, b.priority, (SELECT COUNT(*) FROM COMMENTS WHERE postid = b.seq) as countComment FROM board b INNER JOIN sys_empref e ON b.writerId = e.id left join code_master c on b.processState = c.codeId left outer join SPG.DBO.CRM_USER_MASTER d on b.writerId = d.USR_ID
b.deleteYn = 'N'
AND b.requestDate >= #{startDate}
AND b.requestDate <= #{endDate}
and e.depart1 = ( SELECT depart1 FROM sys_empref WHERE id = #{managerId} )
ORDER BY b.seq DESC
