mapper.select:
SELECT COUNT(*) 
FROM sys_menu_auth
WHERE ID = #{id} AND M_CODE = #{menuCode}

mapper.update:
UPDATE sys_menu_auth
SET AUTH = #{auth}, UPDATE_USER = #{updateUser}, UPDATE_DT = GETDATE()
WHERE ID = #{id} AND M_CODE = #{menuCode}

UPDATE sys_empref
SET AUTH_LEVEL = #{authLevel}, UPDATE_USER = #{updateUser}, UPDATE_DT = GETDATE()
WHERE ID = #{id}
